from sys import flags
from collections import Counter # Helpful class, see documentation or help(Counter)
import a6p1, a6p2, a6p3

LETTER = 'ABCDEFGHIJKLMNOPQRSTUVWXYZ'
ETAOIN = "ETAOINSHRDLCUMWFGYPBVKJXQZ"

def breakSub( ciphertext: str, textFile: "str path to a text file", n: int ) -> str:
    # initial the frequency mapping
    mapping = freqDict(ciphertext)
    #print(mapping)
    # initial n-gram frequency dictionary
    nfrequencydic = a6p1.ngramsFreqsFromFile(textFile, n)
    bestMapping = a6p3.bestSuccessor(mapping, ciphertext, nfrequencydic, n)
    
    # finding bestMapping
    while bestMapping != mapping:
        mapping = bestMapping
        #print(mapping)
        bestMapping = a6p3.bestSuccessor(mapping, ciphertext, nfrequencydic, n)
    #print(bestMapping)
    decipher = translateMessage(bestMapping, ciphertext)
    return decipher

def freqDict(ciphertext):
    """
    Analyze the frequency of the letters
    """ 
    freqDictionary = {}
    cipherlist = list(ciphertext.upper())
    cipherlist.sort()
    listset = set(cipherlist)
    uniquelist = list(listset)
    #print(cipherlist)
    ciphertextCounter = Counter(cipherlist).most_common(len(uniquelist))
    #print(ciphertextCounter)
    
    for i in ciphertextCounter:
        if i[0] in LETTER:
            freqDictionary[i[0]] = i[1]
    for letter in LETTER:
        if letter not in freqDictionary.keys():
            freqDictionary[letter] = 0
    #print(freqDictionary)        
    
    for key in freqDictionary.keys():
        freqDictionary[key] = ETAOIN[list(freqDictionary).index(key)]

    #print(freqDictionary)
    return freqDictionary
     

def translateMessage(mapping, message):
    translated = ''

    # Loop through each symbol in message:
    for symbol in message:
        if symbol in mapping.keys():
            translated += mapping[symbol]
        else:
            translated += symbol

    return translated
    
    
